import React,{ useState } from "react";
function App() {
  const [device,setDivice]=useState(true)
  const[mode,setMode]=useState("desktop")
  const togglefun=()=>{
  device?setMode("mobile"):setMode("desktop")
  device?setDivice(false):setDivice(true)
  }
  return (

  <div className="nav">
    <div className={mode}>
   <p>
   Nav Bar
   </p>
    </div>

      <button className="btn-toggle" onClick={togglefun}>Click me </button>
  </div>
  );
}

export default App;
