import React,{ useState } from "react";
function App() {
  const [toggle,setToggle] = useState({
     device:"default-mobile",
     link:"default-link"
  })
  const togglefun =()=>{
    setToggle({
      device:"mobile-mode",
      link:"nav-link"
  })
  }
  return (
  <div className="nav">
        <ul className={toggle.device}>
         <li className={toggle.link}>
            Home
         </li>
         <li className={toggle.link}>
           About
         </li>
         <li className={toggle.link}>
           Code
         </li>
         <li className={toggle.link}>
           Contact us
         </li>
       </ul>

      <button onClick={togglefun}>Click me </button>
  </div>
  );
}

export default App;
