import React,{ useState } from "react";
function App() {
  const [toggle,setToggle] = useState({
     device:"default",
  })
  const togglefun =()=>{
    setToggle({
      device:"mobile",
  })
  }
  return (
  <div className="nav">
  <div className="Logo">
    Home
  </div>

       <ul className={toggle.device}>
         <li>
            Home
         </li>
         <li>
           About
         </li>
         <li>
           Code
         </li>
         <li>
           Contact us
         </li>
       </ul>

      <button onClick={togglefun}>Click me </button>
  </div>
  );
}

export default App;
